import React, {useState} from "react";
import PropTypes from 'prop-types';

import BookShelfChanger from "./BookShelfChanger";

const BookShelveElement = ({bookID, bookCoverImageUrl, bookTitle, bookAuthors}) => {
  return(
    <li>
      <div className="book">
        <div className="book-top">
          <div
            className="book-cover"
            style={{
              width: 128,
              height: 193,
              backgroundImage:
                `url("${bookCoverImageUrl}")`,
            }}
          ></div>
          <BookShelfChanger/>
        </div>
        <div className="book-title">{bookTitle}</div>
        { bookAuthors.map((author) => (
          <div className="book-authors">{author}</div>
        )) }
      </div>
    </li>
  );
};

BookShelveElement.propTypes = {
  bookCoverImageUrl: PropTypes.string.isRequired,
  bookTitle: PropTypes.string.isRequired,
  bookAuthors: PropTypes.array.isRequired
};

export default BookShelveElement;
