import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

import BookShelve from "../components/BookShelve";
import BookShelveElement from "../components/BookShelveElement";

import * as BooksAPI from "../apis/BooksAPI";

const CURRENTLY_READING_SHELF = "currentlyReading";
const CURRENTLU_READING_TITLE = "Currently Reading";

const MainScreen = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getBooksFromServer = async () => {
      // Get All Book From Server.
      const res = await BooksAPI.getAll();
      // console.log(res);
      setBooks(res);
    };
    getBooksFromServer();
  }, []);

  return (
    <div className="app">
      <div className="list-books">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <div className="list-books-content">
          <div>
            <BookShelve
              bookShelfName={CURRENTLU_READING_TITLE}
              books={books.filter(
                (book) => book.shelf === CURRENTLY_READING_SHELF
              )}
            />
            <div className="bookshelf">
              <h2 className="bookshelf-title">Want to Read</h2>
              <div className="bookshelf-books">
                <ol className="books-grid">

                </ol>
              </div>
            </div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Read</h2>
              <div className="bookshelf-books">
                <ol className="books-grid">

                </ol>
              </div>
            </div>
          </div>
        </div>
        <div className="open-search">
          <Link to="/search">Add a book</Link>
        </div>
      </div>
    </div>
  );
};

export default MainScreen;
